sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},

    //Purpose :- To check if the date is in the past
    //Input :- Value from DateTime field
    //Output :- Boolean
    isPastDate: function(strDate) {
        //Create DateTime objects for current date and the date passed to Script include
        var rightNow = new GlideDateTime();
        var testDate = new GlideDateTime(strDate);

        //if the tesdtDate is befor current date pass true else return false
        if (testDate.before(rightNow)) {
            return true;
        } else {
            return false;
        }
    },

    //Purpose :- To check if the date is of today
    //Input :- Value from DateTime field
    //Output :- Boolean

    isDateToday: function(strDate) {
        //Create DateTime objects for current date and the date passed to Script include
        var rightNow = new GlideDateTime();
        var whenNeededDate = new GlideDateTime(strDate);

        //Get the date part of the DateTime object
        var today = rightNow.getLocalDate();
        var testDate = whenNeededDate.getLocalDate();

        //if the tesdtDate is of today
        if (testDate.compareTo(today) == 0) {
            return true;
        } else {
            return false;
        }


    },

    type: 'NeedItUtils'
};
